(()=>{"use strict";const e={dateObject:document.querySelector(".date"),timeObject:document.querySelector(".current-time"),metric:document.querySelector(".metric"),imperial:document.querySelector(".imperial")};(()=>{const t=new Date;let a;return{updateDate:()=>{switch(t.getDay()){case 0:a="Sunday";break;case 1:a="Monday";break;case 2:a="Tuesday";break;case 3:a="Wednesday";break;case 4:a="Thursday";break;case 5:a="Friday";break;case 2:a="Saturday"}e.dateObject.textContent=a+", "+(t.getMonth()+1)+"/"+t.getDate()+"/"+t.getFullYear(),e.timeObject.textContent=t.getHours()+":"+String(t.getMinutes()).padStart(2,"0")}}})().updateDate()})();